class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
         int ans=0,n=a.size(),m=a[0].size();
        for(int i=0;i<n/2;i++){

            for(int j=0;j<m/2;j++){
                 int c=0;
                if(a[i][j])c++;
                if(a[i][m-1-j])c++;
                if(a[n-1-i][j])c++;
                if(a[n-1-i][m-1-j])c++;
                ans+=min(c,4-c);
            }
        }
        int oo=0,oz=0;

        if(n&1){
        for(int i=0;i<m/2;i++){
            if(a[n/2][i]!=a[n/2][m-1-i])oz++;
            if(a[n/2][i]==a[n/2][m-1-i]&&a[n/2][i])oo++;
        }}
           if(m&1){
        for(int i=0;i<n/2;i++){
            if(a[i][m/2]!=a[n-1-i][m/2])oz++;
            if(a[i][m/2]==a[n-1-i][m/2]&&a[i][m/2])oo++;
        }}
        // cout<<ans<<endl;
        if(oo&1){
            if(oz)ans+=oz;
            else ans+=2;
        }

        else ans+=oz;
        if((n&1)&&(m&1))if(a[n/2][m/2])ans++;

        return ans;
    }
};
