class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        m, n = len(g), len(g[0])
        ans = 0
        if m % 2 == 0 and n % 2 == 0:
            for i in range(m // 2):
                for j in range(n // 2):
                    res = g[i][j] + g[i][n - 1 - j] + g[m - 1 - i][j] + g[m - 1 - i][n - 1 - j]
                    if res == 2:
                        ans += res
                    else:
                        ans += res % 2
            return ans
