/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    let m = grid.length, n = grid[0].length;
    let ans = 0;
    for(let i = 0, j = m - 1; i < j; i++, j--) {
        for(let x = 0, y = n - 1; x < y; x++, y--) {
            let cnt = grid[i][x] + grid[i][y] + grid[j][x] + grid[j][y];

            ans += Math.min(cnt, 4 - cnt);
        }
    }
     if(m%2 && n%2) {
        if(grid[Math.floor(m/2)][Math.floor(n/2)]) {
            ans++;
        }
    }

    let cnt = 0, sum = 0;

    if(m%2) {
        let mid = Math.floor(m/2);
        for(let i = 0, j = n - 1; i < j; i++, j--) {
            if(grid[mid][i] !== grid[mid][j]) {
                cnt++;
                sum += 2;
            } else {
                sum += grid[mid][i]*2
            }
        }
    }
    if(n%2) {
        let mid = Math.floor(n/2);
        for(let i = 0, j = m - 1; i < j; i++, j--) {
            if(grid[i][mid] !== grid[j][mid]) {
                cnt++;
                sum += 2;
            } else {
                sum += grid[j][mid]*2
            }
        }
    }
    if(sum%4 && !cnt) {
        cnt = 2;
    }

    return ans + cnt;
};
