class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int op=0;
        int cnt=0,ex=0;

        for(int i=0;i<n/2;i++){
            int x=0,y=m-1;
            int j=n-i-1;
            while(x<y){
                int c1=0,c0=0;
                if(grid[i][x]==0) c0++;
                else c1++;
                if(grid[i][y]==0) c0++;
                else c1++;
                if(grid[j][x]==0)c0++;
                else c1++;
                if(grid[j][y]==0) c0++;
                else c1++;
                //cout<<c1<<" "<<c0<<endl;
                op+=(4-max(c1,c0));
                x++;y--;
            }

            if(x==y && grid[i][x]==grid[j][x] && grid[i][x]==1) cnt++;
            if(x==y && grid[i][x]!=grid[j][x]){  ex++;op++;}


        }

        if(n&1){
            int i=n/2;
            int x=0,y=m-1;

            while(x<y){
                if(grid[i][x]!=grid[i][y]){ex++; op++;}
                if(grid[i][x]==grid[i][y] && grid[i][x]==1) cnt++;
                x++;y--;
            }

            if(m&1){
                if(grid[i][m/2]==1) op++;
            }
        }
        if((cnt&1) && (ex==0)) op+=2;

        return op;
    }
};
