impl Solution {
    pub fn min_flips(grid: Vec<Vec<i32>>) -> i32 {
        let mut res = 0;

        let h = grid.len();
        let w = grid[0].len();

        for i in 0..h / 2 {
            for j in 0..w / 2 {
                let mut c = 0;
                if grid[i][j] != grid[h - i - 1][j] {
                    c += 1;
                }
                if grid[i][j] != grid[i][w - j - 1] {
                    c += 1;
                }
                if grid[i][j] != grid[h - i - 1][w - j - 1] {
                    c += 1;
                }

                if c > 2 {
                    res += 4-c;
                } else {
                    res += c;
                }
            }
        }

        let mut odds = 0;
        let mut goods = 0;

        if w % 2 != 0 {
            for i in 0..h / 2 {
                if grid[i][w / 2] != grid[h - i - 1][w / 2] {
                    odds += 1
                } else {
                    goods += grid[i][w / 2]
                };
            }
        }

        if h % 2 != 0 {
            for j in 0..w / 2 {
                if grid[h / 2][j] != grid[h / 2][w - j - 1] {
                    odds += 1
                } else {
                    goods += grid[h / 2][j]
                };
            }
        }

        if goods % 2 == 0 {
            res += odds;
        } else {
            if odds > 0 {
                res += odds;
            } else {
                res += 2;
            }
        }

        if w % 2 != 0 && h % 2 != 0 {
            res += grid[h / 2][w / 2];
        }

        res
    }
}
