class Solution {
    public int minFlips(int[][] grid) {
        int ans=0;
        int n=grid.length;
        int m = grid[0].length;
        int i=0;
        int j=n-1;
        int k=0;
        int l=m-1;
        int gfg=0;
        int aa=0;
        while(i<j){
            k=0;
            l=m-1;
            int one=0;
            int zero=0;
            while(k<l){
                one=0;
                one+=(grid[i][k]+grid[i][l]+grid[j][k]+grid[j][l]);
                ans+=Math.min(one,4-one);


                k++;
                l--;
            }
            if(k==l){
                int gg= grid[i][k]+grid[j][l];
                ans+=Math.min(gg,2-gg);
                if(gg==2){
                    gfg+=2;
                }
                 if(gg==1){
                    aa++;
                }
            }
            i++;
            j--;
              System.out.println(i+" "+j+" " +ans);
        }

        int xx=0;
        if(i==j){
            k=0;
            l=m-1;
            while(k<l){
                int gg= grid[i][k]+grid[j][l];
                ans+=Math.min(gg,2-gg);
                if(gg==2){
                    gfg+=2;
                }
                if(gg==1){
                    aa++;
                }
                k++;
                l--;
            }
            if(k==l){
                if(grid[i][k]==1){
                    xx++;
                }
            }
        }

        // if(gfg%4!=0){
        //     ans+=2;
        // }
        ans+=(gfg%4);

        if((gfg%4)==2 && aa>=1){
            ans-=2;
        }
        if(xx==1){
            ans++;
        }
        return ans;
    }
}
