class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int f=0;
        int cnt1=0;
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                vector<int>temp;
                temp.push_back(grid[i][j]);
                temp.push_back(grid[n-i-1][j]);
                temp.push_back(grid[i][m-j-1]);
                temp.push_back(grid[n-i-1][m-j-1]);
                int c0=0;
                int c1=0;
                for(int k=0;k<4;k++){
                    if(temp[k]==0){
                        c0++;
                    }
                    else{
                        c1++;
                    }
                }
                ans+=min(c0,c1);
                if(c0<3){
                    cnt1+=4;
                }
            }
        }
        cout<<ans<<" "<<cnt1<<endl;
        int cnt=0;
        if(n%2!=0){
            int l=0;int r=m-1;
            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]){
                    cnt++;
                }
                else if(grid[n/2][l]==1){
                    cnt1+=2;
                }
                l++;
                r--;
            }
        }
        if(m%2!=0){
            int l=0;int r=n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]){
                    cnt++;
                }
                else if(grid[l][m/2]==1){
                    cnt1+=2;
                }
                l++;
                r--;
            }
        }
        ans+=cnt;
        if(n%2!=0 and m%2!=0){
            if(grid[n/2][m/2]==1){
                cnt1++;
            }
        }
        if(cnt1%4==1){
            ans+=1;
        }
        else if(cnt1%4==2){
            if(cnt==0){
                ans+=2;
            }
        }
        else if(cnt1%4==3){
            if(cnt==0){
                ans+=2;
            }
            ans++;
        }
        return ans;
    }
};
