class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int tot = 0;
        for(int i = 0; i < grid.size() / 2; i++) {
            for(int q = 0; q < grid[i].size() / 2; q++) {
                int cnt = 0;
                cnt += grid[i][q];
                cnt += grid[grid.size() - i - 1][q];
                cnt += grid[i][grid[i].size() - q - 1];
                cnt += grid[grid.size() - i - 1][grid[i].size() - q - 1];
                tot += min(4 - cnt, cnt);
                // cout << cnt << endl;
            }
        }
        int onecnt = 0;
        int forced = 0;
        if (grid[0].size() & 1) {
            for(int i = 0; i < grid.size() / 2; i++) {
                if (grid[i][grid[0].size() / 2] != grid[grid.size() - 1 - i][grid[0].size() / 2]) {
                    // cout << "I " << i << endl;
                    onecnt += 2;
                    forced += 2;
                    tot++;
                }
                else if (grid[i][grid[0].size() / 2] == 1) {
                    onecnt += 2;
                }
            }
        }
        if (grid.size() & 1) {
            for(int i = 0; i < grid[0].size() / 2; i++) {
                if (grid[grid.size() / 2][i] != grid[grid.size() / 2][grid[0].size() - 1 - i]) {
                    onecnt += 2;
                    forced += 2;
                    tot++;
                }
                else if (grid[grid.size() / 2][i] == 1) {
                    onecnt += 2;
                }
            }
        }
        // cout << forced << endl;
        onecnt %= 4;
        if (onecnt == 2 && forced == 0) {
            tot += 2;
        }
        if ((grid.size() & 1) && (grid[0].size() & 1)) {
            tot += (grid[grid.size() / 2][grid[0].size() / 2]);
        }
        return tot;
    }
};
