import java.util.*;

public class lc136 {
}

class Solution {
    void append(List<int[]>a, int [] ele){
        for(int i = 0;i<a.size();i++){
            if(a.get(i)[0] == ele[0] && a.get(i)[1]==ele[1]){
                return;
            }
        }
        a.add(ele);
    }
    public int minFlips(int[][] grid) {
        // 回文 本质是让四个角相同
        int m = grid.length, n = grid[0].length;
        int q = 0, w = 0, e = 0;
        int ans =0 ;
        boolean[][] visible = new boolean[m][n];
        for(int i = 0;i<m;i++){
            for(int j = 0;j<n;j++){
                if(visible[i][j]) continue;
                List<int[]> ttt = new ArrayList<>();
                int[] a = new int[2];
                a[0] = i;a[1] = j;
                append(ttt,a);
                int[] b = new int[2];
                b[0] = m-i-1;b[1] = j;
                int[] c = new int[2];
                append(ttt,b);
                c[0] = i; c[1] = n-j-1;
                append(ttt,c);
                int[] d = new int[2];
                d[0] = m-i-1;d[1] = n-j-1;
                append(ttt, d);
                visible[a[0]][a[1]] = true;
                visible[b[0]][b[1]] = true;
                visible[c[0]][c[1]] = true;
                visible[d[0]][d[1]] = true;
                if(ttt.size()==1){
                    if(grid[i][j]==1) {
                        ans++;
                    }
                }
                if(ttt.size()==2){
                    int sum = 0;
                    for(int t = 0;t<2;t++){
                        int x = ttt.get(t)[0], y = ttt.get(t)[1];
                        sum += grid[x][y];
                    }
                    if(sum==1){
                        q++;
                    }
                    if(sum == 0){
                        w++;
                    }
                    if(sum == 2){
                        e++;
                    }
                }
                if(ttt.size()==4){
                    int cnt1=0, cnt0=0;
                    for(int t = 0;t<4;t++){
                        int x = ttt.get(t)[0], y = ttt.get(t)[1];
                        if(grid[x][y]==1){
                            cnt1++;
                        }else{
                            cnt0++;
                        }
                    }
                    ans += 4 - Math.max(cnt1, cnt0);
                }
            }
        }
        ans += q;
        if(e%2!=0){
            if(q==0){
                ans += 2;
            }
        }
        return ans;
    }
}
