class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size()-1;
        int m=grid[0].size()-1;
        int count1=0,ans=0,countdiff=0;
        if((n+1)%2!=0 && (m+1)%2!=0){
            if(grid[n/2][m/2]!=0){
                grid[n/2][m/2]=0;
                ans++;
            }
        }
        for(int i=0;i<(grid.size()+1)/2;i++){
            for(int j=0;j<(grid[0].size()+1)/2;j++){
                if(j==m-j){
                    if(grid[i][j]!=grid[n-i][j])countdiff++;
                    if(grid[i][j]==1)count1++;
                }
                else if(i==n-i){
                    if(grid[i][j]!=grid[i][m-j])countdiff++;
                    if(grid[i][j]==1)count1++;
                }
                else{
                    int count =grid[i][j]+grid[i][m-j]+grid[n-i][j]+grid[n-i][m-j];
                    ans+=min(count,4-count);

                }
            }

        }
        if(countdiff==0&&count1%2!=0)ans+=2;
        if(countdiff!=0)ans+=countdiff;
        return ans;
    }
};
