class Solution {
public:

    int func(int i , int cnt , vector<pair<int,int>>&arr , vector<vector<int>>&dp ) {
        if( i == arr.size() ) {
            if( cnt % 4 == 0 ) return 0;
            return 1e9;
        }
        if( dp[i][cnt] != -1 ) return dp[i][cnt];

        int ans = 1e9;
        ans = min(ans , arr[i].second - arr[i].first + func(i + 1 , (cnt + arr[i].second)%4 , arr , dp ) );
        ans = min(ans , arr[i].first + func(i + 1 , cnt , arr , dp ) ) ;

        return dp[i][cnt] = ans;
    }
    int minFlips(vector<vector<int>>& grid) {
        vector<pair<int,int>> arr;
        int n = grid.size() , m = grid[0].size();

        for(int i = 0; i < n/2; i++){
            for(int j = 0; j < m/2; j++){
                int tmp = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                arr.push_back({tmp , 4});
            }
        }
        if( n % 2 ) {
            for(int j = 0; j < m/2; j++){
                int tmp = grid[n/2][j] + grid[n/2][m - j - 1];
                arr.push_back({tmp , 2});
            }
        }
        if( m % 2 ) {
            for(int i = 0; i < n/2; i++){
                int tmp = grid[i][m/2] + grid[n - i - 1][m/2];
                arr.push_back({tmp , 2});
            }
        }
        if( n % 2 && m % 2 ) {
            arr.push_back({grid[n/2][m/2] , 1});
        }

        int n1 = arr.size();
        vector<vector<int>>dp(n1 + 1 , vector<int>(4 , -1));

        return func(0 , 0 , arr , dp ) ;
    }
};
