class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0, ans1 = 0;
        int n = grid.length;
        int m = grid[0].length;

        int hn = n / 2;
        int hm = m / 2;
        for (int i = 0; i < hn; i++) {
            for (int j = 0; j < hm; j++) {
                int x = n - i - 1;
                int y = m - j - 1;
                int cnt = 0;
                if (grid[i][j] != grid[i][y]) cnt++;
                if (grid[i][j] != grid[x][j]) cnt++;
                if (grid[i][j] != grid[x][y]) cnt++;
                if (cnt == 1) {
                    ans++;
                    grid[i][j] = 0;
                    grid[i][y] = grid[i][j];
                    grid[x][y] = grid[i][j];
                    grid[x][j] = grid[i][j];
                } else if (cnt == 2) {
                    ans += 2;
                    grid[i][j] = 0;
                    grid[i][y] = grid[i][j];
                    grid[x][y] = grid[i][j];
                    grid[x][j] = grid[i][j];
                } else if (cnt == 3) {
                    ans++;
                    grid[i][j] = 0;
                    grid[i][y] = grid[i][j];
                    grid[x][y] = grid[i][j];
                    grid[x][j] = grid[i][j];
                }
            }
        }

        boolean ok = false;
        if (n % 2 == 1) {
            int i = n / 2;
            for (int j = 0; j < m / 2; j++) {
                int y = m - j - 1;
                if (grid[i][j] != grid[i][y]) {
                    grid[i][j] = 0;
                    grid[i][y] = 0;
                    ok = true;
                    ans++;
                }
            }
        }
        if (m % 2 == 1) {
            int j = m / 2;
            for (int i = 0; i < n / 2; i++) {
                int x = n - i - 1;
                if (grid[i][j] != grid[x][j]) {
                    grid[i][j] = 0;
                    grid[x][j] = 0;
                    ok = true;
                    ans++;
                }
            }
        }

        int cnt =0 ;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1)cnt++;
            }
        }
        cnt = cnt % 4;

        if (cnt == 1) {
            ans++;
        } else if (cnt == 2) {
            if (!ok)ans += 2;
        } else if (cnt == 3) {
            ans++;
            if (!ok)ans += 2;
        }
        return ans;
    }
}
