class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int ans=0;
        int n=v.size();
        int m=v[0].size();
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int p=0;
                if(v[i][j]==0)p+=1;
                if(v[i][m-1-j]==0) p+=1;
                if(v[n-1-i][j]==0) p+=1;
                if(v[n-1-i][m-1-j]==0) p+=1;
                ans+=min(p,4-p);
            }
        }
        if(n%2==1 && m%2==1 && v[n/2][m/2]==1) ans+=1;
        int st=0;
        int en=m-1;
        int cnt=0;
        int ok=0;
        while(n%2==1 && st<en)
        {
            if(v[n/2][st]!=v[n/2][en]) ok+=1;
            else if(v[n/2][st]==1) cnt+=2;
            st+=1;
            en-=1;
        }
        st=0;
        en=n-1;
        while(m%2==1 && st<en)
        {
            if(v[st][m/2]!=v[en][m/2]) ok+=1;
            else if(v[st][m/2]==1) cnt+=2;
            st+=1;
            en-=1;
        }
        ans+=ok;
        if(cnt%4==2)
        {
            if(ok==0) ans+=2;
        }
        return ans;
    }
};
