class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int m = a.size(), n = a[0].size(), ans = 0;
        for(int i = 0; i < m/2; i++) {
            for(int j = 0; j < n/2; j++) {
                int ones = 0;
                if(a[i][j] == 1) ones++;
                if(a[i][n-1-j] == 1) ones++;
                if(a[m-1-i][j] == 1) ones++;
                if(a[m-1-i][n-1-j] == 1) ones++;
                ans += min(ones, 4-ones);
            }
        }
        int x1 = 0, x2 = 0;
        if(m&1) {
            int i = m/2;
            for(int j = 0; j < n/2; j++) {
                if(a[i][j]+a[i][n-1-j] == 2) x1++;
                if(a[i][j]+a[i][n-1-j] == 1) x2++; 
            }
        }
        if(n&1) {
            int j = n/2;
            for(int i = 0; i < m/2; i++) {
                if(a[i][j]+a[m-1-i][j] == 2) x1++;
                if(a[i][j]+a[m-1-i][j] == 1) x2++;
            }
        }
        // when both odd
        if((m&1) and (n&1)) {
            ans += a[m/2][n/2]==1;
        }
        if(x2 > 0) ans += x2;
        if(x2 == 0 and (x1&1)) ans+=2;
        return ans;
    }
};
