class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int flipCount = 0;
        for (int i=0; i<m/2; i++) {
            for (int j=0; j<n/2; j++) {
                int countOne = 0;
                if (grid[i][j] == 1) countOne++;
                if (grid[i][n-1-j] == 1) countOne++;
                if (grid[m-1-i][j] == 1) countOne++;
                if (grid[m-1-i][n-1-j] == 1) countOne++;
                flipCount += countOne<=2 ? countOne : 4-countOne;
            }
        }
        boolean zeroOneFound = false;
        int countOneTwo = 0;
        if (m%2 != 0) {
            for (int j=0; j<n/2; j++) {
                int countOne = 0;
                if (grid[m/2][j] == 1) countOne++;
                if (grid[m/2][n-1-j] == 1) countOne++;
                flipCount += countOne==2 ? 0 : countOne;
                if (countOne == 1) zeroOneFound = true;
                if (countOne == 2) countOneTwo++;
            }
        }
        if (n%2 != 0) {
            for (int i=0; i<m/2; i++) {
                int countOne = 0;
                if (grid[i][n/2] == 1) countOne++;
                if (grid[m-1-i][n/2] == 1) countOne++;
                flipCount += countOne==2 ? 0 : countOne;
                if (countOne == 1) zeroOneFound = true;
                if (countOne == 2) countOneTwo++;
            }
        }
        if (m%2 != 0 && n%2 != 0) {
            flipCount += grid[m/2][n/2];
        }
        return countOneTwo%2 == 0 ? flipCount : (flipCount + (zeroOneFound ? 0 : 2));
    }
}
