class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        rows = len(grid[0])
        cols = len(grid)
        halfRows = ceil(rows / 2)
        halfCols = ceil(cols / 2)
        oddRows = rows % 2 == 1
        oddCols = cols % 2 == 1

        halfFlip = False
        halfOnes = 0

        flips = 0
        for y in range(halfCols):
            for x in range(halfRows):
                if y == halfCols - 1 and oddCols and x == halfRows - 1 and oddRows:
                    flips += grid[y][x]
                elif y == halfCols - 1 and oddCols:
                    halfOnes += grid[y][x] + grid[y][rows - x - 1]
                    if grid[y][x] != grid[y][rows - x - 1]:
                        flips += 1
                        halfFlip = True
                elif x == halfRows - 1 and oddRows:
                    halfOnes += grid[y][x] + grid[cols - y - 1][x]
                    if grid[y][x] != grid[cols - y - 1][x]:
                        flips += 1
                        halfFlip = True
                else:
                    total = grid[y][x] + grid[cols - y - 1][x] + grid[y][rows - x - 1] + grid[cols - y - 1][rows - x - 1]

                    if total == 1 or total == 3:
                        flips += 1
                    elif total == 2:
                        flips += 2

        if halfOnes % 4 == 2 and not halfFlip:
            flips += 2

        return flips
