class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
          int n=grid.size();
        int m=grid[0].size();
        int cnt=0;
        int dCnt=0;
        int dRes=0;
        vector<int> val(2,0);
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                val[0]=0;
                val[1]=0;
                val[grid[i][j]]++;
                val[grid[n-1-i][m-1-j]]++;
                val[grid[i][m-1-j]]++;
                val[grid[n-1-i][j]]++;
                cnt+=min(val[0],val[1]);
            }
        }
        // doing for the divisibility
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)
        {
            grid[n/2][m/2]=0;
            cnt++;
        }
        // cout<<"cnt "<<cnt<<endl;
        if(n%2==1)
        {
            for(int j=0;j<m/2;j++)
            {
                if(grid[n/2][j]!=grid[n/2][m-1-j])
                {
                    grid[n/2][j]=0;
                    grid[n/2][m-1-j]=0;
                    dRes++;
                }
                else if(grid[n/2][j]==1)
                {
                    dCnt++;
                }

            }
        }

         if(m%2==1)
        {

            for(int i=0;i<n/2;i++)
            {
                if(grid[i][m/2]!=grid[n-1-i][m/2])
                {
                    grid[i][m/2]=0;
                    grid[n-1-i][m/2]=0;
                    dRes++;
                }
                else if(grid[i][m/2]==1)
                {
                    dCnt++;
                }

            }
        }
        dCnt*=2;
        // cout<<"dCnt "<<dCnt<<endl;
        // cout<<"dRes "<<dRes<<endl;
        if(dCnt%4==0){
            cnt+=dRes;
        }
        else {
            if(dRes>=1) cnt+=dRes;
            else cnt+=2;

        }
        return cnt;
    }
};
