func minFlips(grid [][]int) (ans int) {
	m, n := len(grid), len(grid[0])
	for i := 0; i < m/2; i++ {
		for j := 0; j < n/2; j++ {
			k := grid[i][j] + grid[m-i-1][j] + grid[i][n-j-1] + grid[m-i-1][n-j-1]
			ans += min(k, 4-k)
		}
	}

	c1, c2 := 0, 0
	if m%2 != 0 {
		for i := 0; i < n/2; i++ {
			if grid[m/2][i] == 1 && grid[m/2][n-i-1] == 1 {
				c1++
			} else if grid[m/2][i] != grid[m/2][n-i-1] {
				c2++
			}
		}
	}
	if n%2 != 0 {
		for i := 0; i < m/2; i++ {
			if grid[i][n/2] == 1 && grid[m-i-1][n/2] == 1 {
				c1++
			} else if grid[i][n/2] != grid[m-i-1][n/2] {
				c2++
			}
		}
	}
	ans += c2
	flag := m%2 != 0 && n%2 != 0 && grid[m/2][n/2] == 1
	if flag {
		ans++
	}

	if c1%2 == 1 && c2 == 0 {
		ans += 2
	}
	return ans
}
