class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int count = 0, ans = 0, c = 0;

        for (int i = 0; i < (n + 1)/2; i++) {
            for (int j = 0; j < (m + 1)/2; j++) {
                int c1 = grid[i][j], c2 = grid[i][m - j - 1];
                int c3 = grid[n - i - 1][j], c4 = grid[n - i - 1][m - j - 1];

                if (i != n - i - 1 && j != m - j - 1) {
                    if (c1 != c2) {
                        if (c1 == c3 && c2 == c4) {
                            ans += 2;
                        } else if (c1 != c3 && c2 != c4) {
                            ans += 2;
                        } else if (c1 != c3 && c2 == c4) {
                            ans++;
                        } else if (c2 != c4 && c1 == c3) {
                            ans++;
                        }
                    } else {
                        if (c1 != c3 && c4 != c2) {
                            ans += 2;
                        } else if (c1 == c3 && c4 != c2) {
                            ans++;
                        } else if (c1 != c3 && c1 == c4) {
                            ans++;
                        }
                    }
                } else if (j != m - j - 1) {
                    if (c1 != c2) {
                        ans++;
                        c++;
                    }
                    if (c1 == 1 || c2 == 1) count += 2;
                } else if (i != n - i - 1) {
                    if (c1 != c3) {
                        ans++;
                        c++;
                    }

                    if (c1 == 1 || c3 == 1) count += 2;
                } else {
                    if (c1 == 1) {
                        ans++;
                    }
                }

                grid[i][j] = 0, grid[i][m - j - 1] = 0;
                grid[n - i - 1][j] = 0, grid[n - i - 1][m - j - 1] = 0;
            }
        }
        if (count % 4 == 0 || c >= 2) return ans;

        return ans + 2 - c*2;
    }
};
