class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int ans1 = 0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int v1 = grid[i][j];
                int v2 = grid[i][n-j-1];
                int v3 = grid[m-i-1][j];
                int v4 = grid[m-i-1][n-j-1];


                ans1+=min(v1+v2+v3+v4, 4-(v1+v2+v3+v4));
            }
        }

        int r=0, c=0;
        if(m%2==1) r++;
        if(n%2==1) c++;

        int ans2 = 0;
        if(r && c){
            int flips = 0;
            int other1 = 0;
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]) flips++;
                else if(grid[m/2][i]==1) other1++;
            }

            // if(other1%2==1){
            //     if(flips==0){
            //         flips+=2;
            //     }
            // }
            // ans2+=flips;


            // flips = 0;
            // other1 = 0;
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]) flips++;
                else if(grid[i][n/2]==1) other1++;
            }

            if(other1%2==1){
                if(flips==0){
                    flips+=2;
                }
            }

            ans2+=flips;

            ans2+=grid[m/2][n/2];
        }
        else if(r){
            int count1 = 0;
            int flips = 0;
            int other1 = 0;
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]) flips++;
                else if(grid[m/2][i]==1) other1++;
            }

            if(other1%2==1){
                if(flips==0){
                    flips+=2;
                }
            }
            ans2+=flips;
        }
        else if(c){
            int count1 = 0;
            int flips = 0;
            int other1 = 0;
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]) flips++;
                else if(grid[i][n/2]==1) other1++;
            }

            if(other1%2==1){
                if(flips==0){
                    flips+=2;
                }
            }
            ans2+=flips;

        }

        return ans1+ans2;
    }
