class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        int o=0,d=0;
        int lc=0,rc=n-1;
        while(lc<rc)
        {
            int lr=0,rr=m-1;
            while(lr<rr)
            {
                int c=grid[lr][lc]+grid[lr][rc]+grid[rr][lc]+grid[rr][rc];
                if(c==1 || c==3)
                ans+=1;
                if(c==2)
                ans+=2;
                lr++;
                rr--;
            }
            lc++;
            rc--;
        }
        if(m%2==1)
        {
            int r=m/2;
            int le=0,ri=n-1;
            while(le<ri)
            {
                if(grid[r][le]!=grid[r][ri])
                {
                    d++;
                    ans++;
                }
                else if(grid[r][le]==1)
                o++;
                le++;
                ri--;
            }
        }
        if(n%2==1)
        {
            int r=n/2;
            int le=0,ri=m-1;
            while(le<ri)
            {
                if(grid[le][r]!=grid[ri][r])
                {
                    d++;
                    ans++;
                }
                else if(grid[ri][r]==1)
                o++;
                le++;
                ri--;
            }
        }
        if(m%2==1 && n%2==1)
        {
            int x=m/2,y=n/2;
            if(grid[x][y]==1)
            ans++;
        }
        if(d==0 && o%2==1)
        ans+=2;
        return ans;
    }
};
