class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int mid_i = a.size() / 2;
        int mid_j = a[0].size() / 2;

        int r = 0;
        for (int i = 0; i < a.size() / 2; ++i) {
            for (int j = 0; j < a[0].size() / 2; ++j) {
                int cnt[2] = {0, 0};
                int p = a.size() - 1 - i;
                int q = a[0].size() - 1 - j;
                cnt[a[i][j]] += 1;
                cnt[a[p][j]] += 1;
                cnt[a[i][q]] += 1;
                cnt[a[p][q]] += 1;
                r += min(cnt[0], cnt[1]); // cicle
            }   
        }

        if (a.size() % 2 == 1 && a[0].size() % 2 == 1) {
            if (a[mid_i][mid_j] == 1) {
                r += 1; // center
            }
        }
        int same_one_pair = 0;
        int diff_pair = 0;
        if (a[0].size() % 2 == 1) {
            for (int i = 0; i < a.size() / 2; ++i) {
                if (a[i][mid_j] == a[a.size() - 1 - i][mid_j]) {
                    if (a[i][mid_j] == 1) {
                        same_one_pair += 1;
                    }
                } else {
                    diff_pair += 1;
                }
            }
        }
        if (a.size() % 2 == 1) {
            for (int j = 0; j < a[0].size() / 2; ++j) {
                if (a[mid_i][j] == a[mid_i][a[0].size() - 1 - j]) {
                    if (a[mid_i][j] == 1) {
                        same_one_pair += 1;
                    }
                } else {
                    diff_pair += 1;
                }
            }
        }
        r += diff_pair; // cross
        if (diff_pair == 0) {
            if (same_one_pair % 2 == 1) {
                r += 2; // force
            }
        }
        return r;
    }
};
