class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int ones = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j];
                ans += min(ones, 4-ones);
            }
        }
        int ones = 0, changes = 0;
        if(n&1){
            int l=0, r=m-1;
            while(l<=r){
                if(grid[n/2][l] != grid[n/2][r])    ans++, changes++;
                else if(grid[n/2][l]==1 && l!=r)   ones += 2;
                l++, r--;
            }
        }
        if(m&1){
            int l=0, r=n-1;
            while(l<=r){
                if(grid[l][m/2] != grid[r][m/2])    ans++, changes++;
                else if(grid[l][m/2]==1 && l!=r)   ones += 2;
                l++, r--;
            }
        }
        if(n&1 && m&1 && grid[n/2][m/2]==1)     ones++;
        if(ones % 4 == 0)   return ans;
        if(ones % 4 == 1){
            if(n&1 && m&1 && grid[n/2][m/2]==1)     return ans + 1;
            else if(n&1 && m&1) ans++;
            if(changes > 0)     return ans;
            else                return ans + 2;
        }
        if(ones % 4 == 2){
            if(changes > 0)     return ans;
            else                return ans + 2;
        }
        if(ones % 4 == 3){
            if(n&1 && m&1 && grid[n/2][m/2]==0)     return ans + 1;
            else if(n&1 && m&1) ans++;
            if(changes > 0)     return ans;
            else                return ans + 2;
        }
        return ans;
    }
};
