class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size()/2;
        int n = grid[0].size()/2;

        int ans = 0;
        for (int i=0; i<m; ++i) {
            for (int j=0; j<n; ++j) {
                int t = grid[i][j] + grid[grid.size()-1-i][j] + grid[i][grid[0].size()-1-j] + grid[grid.size()-1-i][grid[0].size()-1-j];
                if (t==1 || t == 3) ans += 1;
                else if (t == 2) ans += 2;
            }
        }

        int mr = grid.size() - m * 2;
        int nr = grid[0].size() - n * 2;
        int ans_m = 0, ans_n = 0;
        int tm = 0, tn = 0;

        if (mr) {
            for (int j=0; j<n; ++j) {
                if (grid[m][j] != grid[m][grid[0].size()-1-j]) ++ans_m;
                tm += grid[m][j];
            }
            if (nr) ans += grid[m][n];
            else {
                if (ans_m == 0 && tm % 2 != 0) return ans + 2;
            }
        }
        if (nr) {
            for (int i=0; i<m; ++i) {
                if (grid[i][n] != grid[grid.size()-1-i][n]) ++ans_n;
                tn += grid[i][n];
            }
            if (mr) {
                if (ans_m == 0 && ans_n == 0 && (tm + tn) % 2 != 0) return ans + 2;
            }
            else {
                if (ans_n == 0 && tn % 2 != 0) return ans + 2;
            }
        }
        return ans + ans_m + ans_n;
    }
};
