class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n/2; i++){
            for(int j = 0; j < m/2; j++){
                int ones = 0, zero = 0;
                grid[i][j] ? ones++ : zero++;
                grid[i][m-j-1] ? ones++ : zero++;
                grid[n-i-1][j] ? ones++ : zero++;
                grid[n-i-1][m-j-1] ? ones++ : zero++;

                res += min(ones, zero);
            }
        }
        if(n%2 && m%2 && grid[n/2][m/2]) res++;

        int one = 0, zero = 0, c = 0;
        if(m%2){
            for(int i = 0; i < n/2; i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]) c += 2;
                else grid[i][m/2] ? one += 2 : zero += 2;
            }
        }
        if(n%2){
            for(int j = 0; j < m/2; j++){
                if(grid[n/2][j] != grid[n/2][m-j-1]) c += 2;
                else grid[n/2][j] ? one += 2 : zero += 2;
            }
        }
        res += c/2;
        one %= 4;
        if(one == 2 && c < 2) res += 2;
        return res;
    }
};
