class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int a = n/2, b = m/2;

        int ans = 0;
        for (int r = 0; r < b; ++r)
            for (int c = 0; c < a; ++c){
                int s = grid[r][c] + grid[r][n-1-c] + grid[m-1-r][c] + grid[m-1-r][n-1-c];
                ans += min(s, 4-s);
            }

        //cout << ans << endl;

        int ud_c = 0, lr_c = 0, ud_1 = 0, lr_1 = 0;
        if (n%2 == 1){
            for (int r = 0; r < b; ++r){
                int s = grid[r][a] + grid[m-1-r][a];
                ud_c += min(s, 2-s);
                if (s == 2) ud_1 += 2; 
                //cout << r << " " << s << endl;
            }
        }
        //cout << ud_c << " " << ud_1 << endl;

        if (m%2 == 1){
            for (int c = 0; c < a; ++c){
                int s = grid[b][c] + grid[b][n-1-c];
                lr_c += min(s, 2-s);
                if (s == 2) lr_1 += 2; 
            }
        }
        //cout << lr_c << " " << lr_1 << endl;

        if (n%2 == 1 && m%2 == 1) ans += grid[b][a];

        if ((lr_1 + ud_1)%4 != 0 && (ud_c + lr_c) == 0) ans += 2;

        return ans + ud_c + lr_c;
    }
};
