class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        r = 0
        n, m = len(grid), len(grid[0])
        i1, i2, j1, j2 = 0, n-1, 0, m-1
        oc, cc = 0, 0
        while i1<i2:
            j1, j2 = 0, m-1
            while j1<j2:
                c1, c2,  c3, c4 = grid[i1][j1], grid[i1][j2], grid[i2][j1], grid[i2][j2]
                c = c1+c2+c3+c4
                nc = 4-c
                if c>nc: c=nc
                r+=c
                j1+=1
                j2-=1
            if j1==j2:
                if grid[i1][j1]!=grid[i2][j1]:
                    cc+=1
                    r+=1
                elif grid[i1][j1]==1:
                    oc+=1
            i1+=1
            i2-=1
        if i1==i2:
            j1, j2 = 0, m-1
            while j1<j2:
                if grid[i1][j1]!=grid[i1][j2]:
                    r+=1
                    cc+=1
                elif grid[i1][j1]==1:
                    oc+=1
                j1+=1
                j2-=1
            if j1==j2 and grid[i1][j1]==1:
                r+=1
        if (oc&1) and cc==0:
            r+=2
        return r
