class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int r = 0;
        for(int i=0;i<n-i-1;i++) {
            for(int j=0;j<m-j-1;j++) {
                int t = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1]+grid[n-i-1][m-j-1];
                r += Math.min(t,4-t);
            }
        }
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1) r++;
        int c = 0,d = 0;
        if(n%2 == 1) {
            for(int i=0;i<m-i-1;i++) {
                if(grid[n/2][i] != grid[n/2][m-i-1]) {
                    d++;
                } else if(grid[n/2][i] == 1) {
                    c++;
                }
            }
        } 
        if(m%2 == 1) {
            for(int i=0;i<n-i-1;i++) {
                if(grid[i][m/2] != grid[n-i-1][m/2]) {
                    d++;
                } else if(grid[i][m/2] == 1) {
                    c++;
                }
            }
        }
        if(d>0) {
            r += d;
        } else if(c%2==1) {
            r += 2;
        }
        return r;
    }
}
