class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int m = grid.size() ;
        int n = grid[0].size() ;

        int ans = 0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int zeros = 0, ones = 0;
                if(grid[i][j])
                    ones++ ;
                else
                    zeros++ ;

                if(grid[i][n-1-j])
                    ones++ ;
                else
                    zeros++ ;

                if(grid[m-1-i][j])
                    ones++ ;
                else
                    zeros++ ;

                if(grid[m-1-i][n-1-j])
                    ones++ ;
                else
                    zeros++ ;

                ans += 4-max(ones,zeros) ;
            }
        }

        if(m%2==0 && n%2==0)
            return ans ;

        int ones=0,swaps=0;
        if(m%2)
        {
            for(int j=0;j<n/2;j++)
            {
                if(grid[m/2][j] && (grid[m/2][j] == grid[m/2][n-1-j]))
                    ones++ ;
                else if(grid[m/2][j] != grid[m/2][n-1-j])
                    swaps++ ;
            }
        }
        if(n%2)
        {
        for(int i=0;i<m/2;i++)
        {
            if(grid[i][n/2] && (grid[i][n/2] == grid[m-1-i][n/2]))
                ones++ ;
            else if(grid[i][n/2] != grid[m-1-i][n/2])
                swaps++ ;
        }

        // cout<<ans<<" "<<swaps<<" "<<ones<<endl;

        }

        if(swaps>0 || (ones%2==0))
            ans= ans+swaps ;   
        else
            ans = ans+swaps+2 ;

        if(m%2 && n%2)
            return ans += grid[m/2][n/2] ? 1 : 0 ;

        return ans ;
    }
};
