class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int cnt = 0;
        int ocnt = 0;
        int change = 0;
        for (int i = 0; i < (m+1)/2; i++) {
            for (int j = 0; j < (n+1)/2; j++) {
                int i2 = m - 1 - i;
                int j2 = n - 1 - j;
                int ones = grid[i][j] + grid[i2][j] + grid[i][j2] + grid[i2][j2];
                if (i != i2 && j != j2) {
                    cnt += Math.min(ones, 4 - ones);
                } else if (i == i2 && j == j2) {
                    cnt += ones / 4;
                } else {
                    if (grid[i][j] != grid[i2][j2]) {
                        cnt++;
                        change++;
                    } else ocnt += grid[i][j];
                }
            }
        }
        if (ocnt % 2 != 0 && change == 0) cnt += 2;
        return cnt;
    }
}
