class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int cnt = 0;
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int cnt1 = 0, cnt2 = 0;
                if(grid[i][j] == 1) {
                    cnt1++;
                }
                else {
                    cnt2++;
                }
                if(grid[n-1-i][j] == 1) {
                    cnt1++;
                }
                else {
                    cnt2++;
                }
                if(grid[i][m-1-j] == 1) {
                    cnt1++;
                } 
                else {
                    cnt2++;
                }
                if(grid[n-i-1][m-j-1] == 1) {
                    cnt1++;
                }
                else {
                    cnt2++;
                }
                cnt += min(cnt1, cnt2);
            }
        }
        int add1 = 0, add2 = 0, cnt1 = 0, cnt2 = 0;
        if(n%2) {
            cout<<"YES1\n";
            for(int j=0;j<m/2;j++) {
                if(grid[n/2][j] != grid[n/2][m-j-1]) {
                    cnt1++;
                }
                if(grid[n/2][j] == grid[n/2][m-j-1] && grid[n/2][j]) {
                    add1+=2;
                }
            }
        }
        if(m%2) {
            cout<<"YES2\n";
            for(int i=0;i<n/2;i++) {
                if(grid[i][m/2] != grid[n-i-1][m/2]) {
                    cnt1++;
                }
                if(grid[i][m/2] && grid[i][m/2] == grid[n-i-1][m/2]) {
                    add1+=2;
                }
            }
        }
        cout<<add1<<" "<<add2<<"\n";
        if(add1%4) {
            if(cnt1 > 0) {
                 // cnt+=1;
                // continue;

            }
            else {
                cnt += add1%4;
            }
        }
        // if(add2%4) {
        //     if(cnt2 > 0) {
        //         // continue;
        //         // cnt+=1;
        //     }
        //     else {
        //         cnt += add2%4;
        //     }
        // }
        if(m%2 && n%2) {
            if(grid[n/2][m/2]) {
                cnt++;
            } 
        }
        return cnt+cnt1+cnt2;
    }
};
