class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int m = grid.size(), n = grid[0].size();
        int ret = 0;
        for (int i=0; i<m/2; i++)
            for (int j=0; j<n/2; j++)
            {         
                int count1=0;
                count1 += (grid[i][j]==1);
                count1 += (grid[m-1-i][j]==1);
                count1 += (grid[i][n-1-j]==1);
                count1 += (grid[m-1-i][n-1-j]==1);
                if (count1==1) ret++;
                else if (count1==2) ret+=2;
                else if (count1==3) ret+=1;
            }

        int one = 0, zero = 0, half = 0;
        if (m%2==1)
        {
            for (int j=0; j<n/2; j++)
            {
                if (grid[m/2][j]!=grid[m/2][n-1-j])
                    half += 1;
                else if (grid[m/2][j]==0)
                    zero++;
                else
                    one++;
            }            
        }
        if (n%2==1)
        {
            for (int i=0; i<m/2; i++)
            {
                if (grid[i][n/2]!=grid[m-1-i][n/2])
                    half += 1;
                else if (grid[i][n/2]==0)
                    zero++;
                else
                    one++;
            }            
        }
        int center = -1;
        if (m%2==1 && n%2==1)
        {
            if (grid[m/2][n/2]==1) center = 1;
            else center = 0;
        }
        ret += half;



        int cur = one*2+half*2+(center==1);

        // cout<<ret<<":"<<one<<" "<<half<<" "<<center<<":"<<cur<<endl;

        if (cur%4==0)
            return ret;
        else if (cur%4==1)
        {
            return ret+1;
        }
        else if (cur%4==2)
        {
            if (half>0) return ret;
            else return ret+2;            
        }
        else
        {
            if (half > 0) return ret+1;
            else return ret+3;
        }
    }
};
