class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        #groups of 4
        groups = []
        for i in range(len(grid)//2):
            for j in range(len(grid[0])//2):
                groups.append(grid[i][j]+grid[len(grid)-1-i][j]+grid[len(grid)-1-i][len(grid[0])-1-j]+grid[i][len(grid[0])-1-j])
        groups = [g%4 for g in groups]


        horizontalGroups = []
        if len(grid)%2 == 1:

            for j in range(0, len(grid[0])//2):
                horizontalGroups.append(grid[len(grid)//2][j] + grid[len(grid)//2][len(grid[0])-1-j])
        verticalGroups = []
        if len(grid[0])%2 == 1:

            for j in range(0, len(grid)//2):
                verticalGroups.append(grid[j][len(grid[0])//2] + grid[len(grid)-1-j][len(grid[0])//2])
        oneMid = 0
        if len(grid[0])%2 == 1 and len(grid)%2 == 1:
            oneMid = grid[len(grid)//2][len(grid[0])//2]

        #ultimately we need to get everything except the middle one to 0 or 4, and 0 or 2 respectively
        #do we do dp?
        #ultimately the first array will always = 0 mod 4
        #the second array will then need to be 2 or 4 mod 4. If its length is > 2, 
        #given an array of 0s, ones, and 2s, figure out 

        ans = 0
        for i in range(len(groups)):
            if groups[i] == 1 or groups[i] == 3:
                ans += 1
            elif groups[i] == 2:
                ans += 2
        ans += oneMid
        zeros = 0
        ones = 0
        twos = 0
        twogroups = horizontalGroups + verticalGroups
        for i in range(len(twogroups)):
            if twogroups[i] == 0:
                zeros += 1
            elif twogroups[i] == 1:
                ones += 1
            elif twogroups[i] == 2:
                twos += 1

        #all the ones will cost 1, and can be changed to 0
        #all the zeros won't affect anything

        twos = twos % 2
        if twos == 0:

            ans += ones
            return ans
        if twos == 1:
            #you can convert a 1 to a 2

            if ones > 0:
                ans += ones
                return ans
            else:

                ans += 2
                return ans

        return ans
