class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        int ans=0;
        for(int i=0;i<n/2;++i){
            for(int j=0;j<m/2;++j){
                int num0=0;
                int num1=0;
                g[i][j]?num1++:num0++;
                g[n-1-i][j]?num1++:num0++;
                g[i][m-1-j]?num1++:num0++;
                g[n-1-i][m-1-j]?num1++:num0++;
                ans+=min(num0,num1);
            }
        }
        //cout<<ans;
        if(n%2&&m%2){
            //统计0-1不等的数量
            int num=0;
            //统计1的数量
            int num1=0;
            if(g[n/2][m/2]==1) ans++;
            for(int i=0;i<n/2;++i){
                if(g[i][m/2]!=g[n-1-i][m/2]) num++;
                else if(g[i][m/2]) num1+=2;
            }
            for(int i=0;i<m/2;++i){
                if(g[n/2][i]!=g[n/2][m-1-i]) num++;
                else if(g[n/2][i]) num1+=2;
            }
            if(num||num1%4==0) ans+=num;
            else ans+=2;
        }else if(m%2){
            //统计0-1不等的数量
            int num=0;
            //统计1的数量
            int num1=0;
            for(int i=0;i<n/2;++i){
                if(g[i][m/2]!=g[n-1-i][m/2]) num++;
                else if(g[i][m/2]) num1+=2;
            }
            if(num||num1%4==0) ans+=num;
            else ans+=2;
        }else if(n%2){
            //统计0-1不等的数量
            int num=0;
            //统计1的数量
            int num1=0;
            for(int i=0;i<m/2;++i){
                if(g[n/2][i]!=g[n/2][m-1-i]) num++;
                else if(g[n/2][i]) num1+=2;
            }
            if(num||num1%4==0) ans+=num;
            else ans+=2;
        }
        return ans;
    }
};
