class Solution {
public:
    int minFlips(vector<vector<int>>& A) {
        int ans=0;
        int n=A.size();
        int m=A[0].size();
        int sum1[2],sum2[3],sum4[5];
        for (int i=0;i<2;++i) sum1[i]=0;
        for (int i=0;i<3;++i) sum2[i]=0;
        for (int i=0;i<5;++i) sum4[i]=0;
        for (int l=0,r=n-1;l<=r;++l,--r)
        {
            for (int i=0,j=m-1;i<=j;++i,--j)
            {
                if (l==r)
                {
                    if (i!=j)
                    {
                        int cnt=0;
                        cnt+=A[l][i];
                        cnt+=A[l][j];
                        sum2[cnt]++;
                    }
                    else
                    {
                        sum1[A[l][i]]++;
                    }
                }
                else
                {
                    if (i==j)
                    {
                        int cnt=0;
                        cnt+=A[l][i];
                        cnt+=A[r][i];
                        sum2[cnt]++;
                    }
                    else
                    {
                        int cnt=0;
                        cnt+=A[l][i];
                        cnt+=A[l][j];
                        cnt+=A[r][i];
                        cnt+=A[r][j];
                        sum4[cnt]++;
                    }
                }
            }
        }
        for (int i=0;i<5;++i) ans+=min(i,4-i)*sum4[i];
        ans+=sum2[1];
        if (sum2[2]%2==1)
        {
            if (sum2[1]==0) ans+=2;
        }
        ans+=sum1[1];
        return ans;
    }
};
