class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans =0;
        for(int i=0;i<(m/2);i++){
            for(int j=0;j<(n/2);j++){
                int s = grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                if(s == 1 || s == 3){
                    ans++;
                }
                else if(s ==2){
                    ans = ans +2;
                }
            }
        }
        int ones =0;
        bool atl= false;
        if(m%2 == 1){
            int start=0,end=n-1;
            while(start<end){
                if(grid[m/2][start]!=grid[m/2][end]){
                    ans ++;
                    atl=true;
                }
                else if(grid[m/2][start]==1 && grid[m/2][end]==1){
                    ones++;
                }
                start++;
                end--;
            }

        }
        if(n%2==1){
            int start=0,end=m-1;
            while(start<end){
                if(grid[start][n/2]!=grid[end][n/2]){
                    ans++;
                    atl=true;
                }
                else if(grid[start][n/2] == 1 && grid[end][n/2] ==1){
                    ones++;
                }
                start++;
                end--;
            }
        }
        if(ones%2==1){
            if(!atl){
                ans = ans+2;
            }
        }
        if(m%2 ==1 && n%2==1){
            if(grid[m/2][n/2]==1){
                ans++;
            }
        }
        return ans;
    }
};
