class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
         int rows = grid.size();
    int cols = grid[0].size();

    int flipCount = 0;
    for (int r = 0; r < rows >> 1; ++r) {
        for (int c = 0; c < cols >> 1; ++c) {
            int cellVal = grid[r][c];
            int quadrants = 1;
            if (!(grid[rows - r - 1][c] - cellVal)) {
                quadrants+=1;
            }
            if (!(grid[r][cols - c - 1] -cellVal)) {
                quadrants+=1;
            }
            if (!(grid[rows - r - 1][cols - c - 1] - cellVal)) {
                quadrants+=1;
            }
            flipCount += quadrants < 4 - quadrants? quadrants:4-quadrants;
        }
    }

    int midRowFlips = 0, midColFlips = 0;
    int extraRow = 0, extraCol = 0;
    if (rows & 1) {
        for (int c = 0; c < cols >> 1; ++c) {
            if (grid[rows >> 1][c] != grid[rows >> 1][cols - c - 1]) {
                midRowFlips+=1;
            } else if (grid[rows >> 1][c] == 1) {
                extraRow += 2;
            }
        }
    }
    flipCount += midRowFlips;

    if (cols & 1) {
        for (int r = 0; r < rows >> 1; ++r) {
            if (grid[r][cols >> 1] != grid[rows - r - 1][cols >> 1]) {
                midColFlips+=1;
            } else if (grid[r][cols >> 1] == 1) {
                extraCol += 2;
            }
        }
    }
    if (rows & 1 && cols & 1 && grid[rows >> 1][cols >> 1]) {
        flipCount+=1;
    }
     if ((extraRow + extraCol) & 3 && !(midRowFlips + midColFlips)) {
        flipCount += 2;
    }
    flipCount += midColFlips;
    return flipCount;
    }
};
