class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int halfr = m/2;
        int halfc = n/2;
        int totalans  = 0;
        for(int i=0;i<halfr;i++){
            for(int j=0;j<halfc;j++){
                int cnt = 0;
                if(grid[i][j]) cnt++;
                if(grid[i][n-j-1]) cnt++;
                if(grid[m-i-1][j]) cnt++;
                if(grid[m-i-1][n-j-1]) cnt++;
                totalans += min(4-cnt,cnt);
            }
        }
        bool roweven = (halfr*2==m);
        bool coleven = (halfc*2==n);
        if(roweven && coleven) return totalans;
        if(!roweven && !coleven){
            totalans += grid[halfr][halfc];
            int pairmatch = 0; int pairnotmatch =0;
            for(int j=0;j<halfc;j++){
                if(grid[halfr][j]==grid[halfr][n-1-j]){
                    if(grid[halfr][j]) pairmatch++;
                }else pairnotmatch++; 
            }
            for(int i=0;i<halfr;i++){
                if(grid[i][halfc]==grid[m-i-1][halfc]){
                    if(grid[i][halfc]) pairmatch++;
                }else pairnotmatch++; 
            }
            totalans += pairnotmatch;
            if(pairmatch%2==1 && !pairnotmatch) totalans += 2;
            return totalans;
        }
        else if(!roweven){
            int pairmatch = 0; int pairnotmatch =0;
            for(int j=0;j<halfc;j++){
                if(grid[halfr][j]==grid[halfr][n-1-j]){
                    if(grid[halfr][j]) pairmatch++;
                }else pairnotmatch++; 
            }
            totalans += pairnotmatch;
            if(pairmatch%2==1 && !pairnotmatch) totalans += 2;
            return totalans;
        }
        else if(!coleven){
            int pairmatch = 0; int pairnotmatch =0;
            for(int i=0;i<halfr;i++){
                if(grid[i][halfc]==grid[m-i-1][halfc]){
                    if(grid[i][halfc]) pairmatch++;
                }else pairnotmatch++; 
            }
            totalans += pairnotmatch;
            if(pairmatch%2==1 && !pairnotmatch) totalans += 2;
            return totalans;
        }
        return -1;
    }
};
