class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int count = 0;
                if(grid[i][j] == 1) count++;
                if(grid[i][m - j - 1] == 1) count++;
                if(grid[n - i - 1][j] == 1) count++;
                if(grid[n - i - 1][m - j - 1] == 1) count++;
                ans += Math.min(count, 4 - count);
            }
        }
        if(n % 2 != 0 && m % 2 != 0){
            if(grid[n / 2][m / 2] == 1) ans++;
            int count = 0;
            int count2 = 0;
            for(int i = 0; i < m / 2; i++){
                int l = grid[n / 2][i];
                int r = grid[n / 2][m - i - 1];
                if(l != r) {
                    count++;
                }else if(l == 1){
                    count2++;
                }
            }
            for(int i = 0; i < n / 2; i++){
                int l = grid[i][m / 2];
                int r = grid[n - i - 1][m / 2];
                if(l != r) {
                    count++;
                }else if(l == 1){
                    count2++;
                }
            }
            if(count2 % 2 != 0 && count == 0){
                ans += 2;
            }else{
                ans += count;
            }
        }else if(n % 2 != 0){
            int count = 0;
            int count2 = 0;
            for(int i = 0; i < m / 2; i++){
                int l = grid[n / 2][i];
                int r = grid[n / 2][m - i - 1];
                if(l != r) {
                    count++;
                }else if(l == 1){
                    count2++;
                }
            }
            if(count2 % 2 != 0 && count == 0){
                ans += 2;
            }else{
                ans += count;
            }
        }else if(m % 2 != 0){
            int count = 0;
            int count2 = 0;
            for(int i = 0; i < n / 2; i++){
                int l = grid[i][m / 2];
                int r = grid[n - i - 1][m / 2];
                if(l != r) {
                    count++;
                }else if(l == 1){
                    count2++;
                }
            }
            if(count2 % 2 != 0 && count == 0){
                ans += 2;
            }else{
                ans += count;
            }
        }
        return ans;
    }
