class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0, sans = 0;
        for (int i = 0; i < n/2; i++) {
            for (int j = 0; j < m/2; j++) {
                int cur = g[i][j] + g[i][m-j-1] + g[n-i-1][j] + g[n-i-1][m-j-1];
                ans += min(cur, 4-cur);
            }
        }
        if (n & 1) {
            for (int j = 0; j < m/2; j++) {
                if (g[n/2][j] != g[n/2][m-j-1]) sans++;
            }
        }
        if (m & 1) {
            for (int i = 0; i < n/2; i++) {
                if (g[i][m/2] != g[n-i-1][m/2]) sans++;
            }
        }
        if (((n & 1) || (m & 1)) && sans < 1) {
            int cnt1 = 0;
            if (n & 1) cnt1 += count(g[n/2].begin(), g[n/2].end(), 1)/2 * 2;
            if (m & 1) for (int i = 0; i < n; i++) cnt1 += g[i][m/2];
            cnt1 = cnt1/2 * 2;
            if (cnt1 % 4) sans += 2;
        }
        ans += sans;
        return ans + (n % 2 && m % 2 && g[n/2][m/2]);
    }
private:
    int cntFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m/2; j++) {
                if (g[i][j] != g[i][m-j-1]) ans++;
            }
        }
        if (!ans) {
            int cnt1 = 0;
            for (int i = 0; i < n; i++) {
                cnt1 += count(g[i].begin(), g[i].end(), 1);
            }
            return min(cnt1%4, 4 - cnt1%4);
        }
        int mcnt = 0;
        if (m & 1) {
            for (int i = 0; i < n; i++) {
                mcnt += g[i][m/2];
            }
        }
        return ans + mcnt%2;
    }
};
