int minFlips(int** grid, int gridSize, int* gridColSize) {
    int flip_count = 0;
    int one = 0;
    bool flag = false;
    for(int left = 0, right = gridSize-1; left <= right; left++, right--){
        for(int top = 0, bot = gridColSize[left]-1; top <= bot; top++, bot--){
            int tmp = 0;
            if(left == right && top == bot){
                if(grid[left][top]){
                    flip_count++;
                }
                continue;
            }
            if(left == right){
                if(grid[left][top] != grid[left][bot]){
                    flip_count++;
                    one += 2;
                    flag = true;
                }
                else if(grid[left][top] == 1){
                    one += 2;
                }
                continue;
            }
            if(top == bot){
                if(grid[left][top] != grid[right][bot]){
                    flip_count++;
                    one += 2;
                    flag = true;
                }
                else if(grid[left][top] == 1){
                    one += 2;
                }
                continue;
            }
            if(grid[left][top]){
                tmp++;
            }
            if(grid[left][bot]){
                tmp++;
            }
            if(grid[right][top]){
                tmp++;
            }
            if(grid[right][bot]){
                tmp++;
            }
            if(tmp % 4 == 0){//0 or 4
                continue;
            }
            if(tmp == 2){
                flip_count += tmp;
            }
            else{
                flip_count++;
            }
        }
    }
    if(one % 4 == 2 && flag == false){
        flip_count += 2;
    }
    return flip_count;
}
