class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int swap=0;
        int row1=0,row2=grid.size()-1;
        int ans=0,ones=0;
        while(row1<row2){
            int col1=0,col2=grid[0].size()-1;
            while(col1<col2){
                int up1=grid[row1][col1];
                int up2=grid[row1][col2];
                int down1=grid[row2][col1];
                int down2=grid[row2][col2];
                int temp=up1+up2+down1+down2;
                if(temp==4 || temp==0)
                {
                    col1++;
                    col2--;
                    continue;
                }
                //switch all to zero or switch all to 1's
                if(temp<=2){
                    //switch to 0
                    grid[row1][col1]=0;
                    grid[row1][col2]=0;
                    grid[row2][col1]=0;
                    grid[row2][col2]=0;
                    ans+=temp;
                }
                else{
                    //switch to 1
                    grid[row1][col1]=1;
                    grid[row1][col2]=1;
                    grid[row2][col1]=1;
                    grid[row2][col2]=1;
                    ans+=1;

                }
                col1++;
                col2--;
            }
            if(col1==col2 && grid[row1][col1]!=grid[row2][col1]){
                grid[row1][col1]=1;
                grid[row2][col1]=1;
                swap++;
                ans++;
            }
            row1++;
            row2--;
        }
        if(row1==row2){
            int col1=0,col2=grid[0].size()-1;
            while(col1<col2){
                if(grid[row1][col1]!=grid[row1][col2]){
                    grid[row1][col1]=1;
                    grid[row1][col2]=1;
                    swap++;
                    ans++;
                }

                col1++;
                col2--;
            }
        }

        for(int i=0;i<grid.size();i++)
        for(int j=0;j<grid[0].size();j++)
        ones+=grid[i][j];

        if(ones%2==1){
            ones--;
            ans++;
        }

        if(ones%4==0)
        return ans;

        if(swap>0)
        return ans;

        return ans+2;
    }
};
