class Solution:
  def minFlips(self, grid: List[List[int]]) -> int:
    n = len(grid)
    m = len(grid[0])
    ret = 0
    for i in range(n // 2):
      for j in range(m // 2):
        x = grid[i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][j] + grid[n - 1 - i][m - 1 - j]
        ret += min(x, 4 - x)
    if n % 2 and m % 2:
      c = o = z = 0
      for j in range(m // 2):
        if grid[n // 2][j] != grid[n // 2][m - 1 - j]:
          c += 1
          ret += 1
        else:
          if grid[n // 2][j]:
            o += 2
          else:
            z += 2
      for i in range(n // 2):
        if grid[i][m // 2] != grid[n - 1 - i][m // 2]:
          c += 1
          ret += 1
        else:
          if grid[i][m // 2]:
            o += 2
          else:
            z += 2
      if grid[n // 2][m // 2]:
        o += 1
      if o % 2:
        ret += 1
        o -= 1
      if o % 4:
        if not c:
          ret += 2
    elif n % 2:
      c = o = z = 0
      for j in range(m // 2):
        if grid[n // 2][j] != grid[n // 2][m - 1 - j]:
          c += 1
          ret += 1
        else:
          if grid[n // 2][j]:
            o += 2
          else:
            z += 2
      if o % 4:
        if not c:
          ret += 2
    elif m % 2:
      c = o = z = 0
      for i in range(n // 2):
        if grid[i][m // 2] != grid[n - 1 - i][m // 2]:
          c += 1
          ret += 1
        else:
          if grid[i][m // 2]:
            o += 2
          else:
            z += 2
      if o % 4:
        if not c:
          ret += 2
    return ret
