class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m,n;
        m=grid.size();
        n=grid[0].size();
        int op=0;
        for(int i=0;i<(m)/2;i++){
            for(int j=0;j<(n)/2;j++){
                int cnt1=0;
                if(grid[i][j]==1)cnt1++;
                if(grid[i][n-1-j]==1)cnt1++;
                if(grid[m-1-i][j]==1)cnt1++;
                if(grid[m-1-i][n-1-j]==1)cnt1++;
                op+=min(cnt1,4-cnt1);
            }
        }
        int change=0;
        if(n%2&&m%2){
            if(grid[m/2][n/2]==1)op++;
        }
        int cnt=0;
        if(m%2){
            int l=0;
            int r=n-1;
            while(l<r){
                if(grid[m/2][l]==1||grid[m/2][r]==1)cnt+=2;
                if(grid[m/2][l]!=grid[m/2][r]){

                    op++;
                    change++;
                }
                l++;r--;
            }
        }

        if(n%2){
            int u=0;
            int d=m-1;
            while(u<d){
                if(grid[u][n/2]==1||grid[d][n/2]==1)cnt+=2;
                if(grid[u][n/2]!=grid[d][n/2]){
                    op++;
                    change++;
                }
                u++;d--;
            }
        }
        if(cnt%4!=0){
            if(change>0);
            else op+=2;
        }

        return op;
    }
};
