class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int flip2 = 0;
        int count2 = 0;
        int rows = grid.size();
        int cols = grid[0].size();
        int rowCount = 0;
        int colCount = 0;
        for (int i = 0; i <= rows / 2; i++) {
            for (int j = 0; j <= cols / 2; j++) {
                int otherI = rows - i - 1;
                int otherJ = cols - j - 1;
                if (otherI < i || otherJ < j) {
                    continue;
                }
                if (i == otherI && j == otherJ) {
                    res += grid[i][j];
                } else if (i == otherI) {
                    if (grid[i][j] != grid[i][otherJ]) {
                        flip2 += 1;
                        res += 1;
                    }
                    else if (grid[i][j] + grid[i][otherJ] == 2) {
                        count2 += 1;
                    }
                } else if (j == otherJ) {
                    if (grid[i][j] != grid[otherI][j]) {
                        flip2 += 1;
                        res += 1;
                    }
                    else if (grid[i][j] + grid[otherI][j] == 2) {
                        count2 += 1;
                    }
                } else {
                    int temp = 0;
                    temp += grid[i][j];
                    temp += grid[otherI][j];
                    temp += grid[i][otherJ];
                    temp += grid[otherI][otherJ];
                    res += min(temp, 4 - temp);
                }
                // cout << i << " " << j << " " << res << endl;
            }
        }
        if (flip2 == 0 && count2 % 2) {
            res += 2;
        }
        return res;
    }
};
