class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        colans=0
        rowans=0
        R=len(grid)
        C=len(grid[0])
        ans=0
        if (R*C)%2==1 and grid[R//2][C//2]==1:
            ans+=1
            grid[R//2][C//2]=0
        total=0
        for r in range(R):
            for c in range(C):
                if grid[r][c]==1:
                    total+=1
        for r in range(R//2):
            c=0
            while c<C//2:
                ones=0
                zeros=0
                x=[]
                x.append(grid[r][c])
                x.append(grid[r][C-c-1])
                x.append(grid[R-r-1][c])
                x.append(grid[R-r-1][C-c-1])
                for x1 in x:
                    if x1==1:
                        ones+=1
                    else:
                        zeros+=1
                ans+=min(zeros, ones)
                if ones>zeros:
                    total+=zeros
                else:
                    total-=ones
                c+=1
                print(x, ans)
        op=0
        if R%2==1:
            left=0
            right=C-1
            while left<right:
                if grid[R//2][left]!=grid[R//2][right]:
                    op+=1
                left+=1
                right-=1
        if C%2==1:
            left=0
            right=R-1
            while left<right:
                if grid[left][C//2]!=grid[right][C//2]:
                    op+=1
                left+=1
                right-=1
        if total%4==2 and op==0:
            return ans+2
        return ans+op
