class Solution {
public:
    int total(vector<vector<int>>& grid)
    {
          int ans=0;
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0;i<(n)/2;i++){
          for(int j=0;j<(m)/2;j++){
              int z=0,o=0;
              if(grid[i][j]==0)
                  z++;
              else o++;
              if(grid[i][m-1-j]==0)
                  z++;
              else
                  o++;
              if(grid[n-1-i][j]==0)
                  z++;
              else
                  o++;
              if(grid[n-1-i][m-1-j]==0)
                  z++;
              else
                  o++;

              if(z==0||o==0)
                  continue;
             if(z==o)
                 ans+=2;
              else
                  ans++;


          }
        }
        int tobeChange=0;
             int one=0;
        if(n%2==1){
            for(int j=0;j<(m)/2;j++){
                int i=n/2;
                if(grid[i][j]!=grid[i][m-1-j])
                    tobeChange++;
                    if(grid[i][j]==1)
                        one++;
                if(grid[i][m-1-j]==1)
                    one++;

            }

        }
             if(m%2==1){
                  for(int j=0;j<(n)/2;j++){
                if(grid[j][m/2]!=grid[n-1-j][m/2])
                    tobeChange++;
                    if(grid[j][m/2]==1)
                        one++;
                if(grid[n-1-j][m/2]==1)
                    one++;               
            }
                 if(n%2==1&&grid[n/2][m/2]==1){
                     ans++;
                 }
             }

       one = one%4;
        one = one%4;
         one = one%4;
        one = one%4;
         one = one%4;
        one = one%4;
         one = one%4;
        one = one%4;
       if(tobeChange+one>=4||one==0||tobeChange==1||tobeChange==2)
           return ans+tobeChange;
        else return ans+2;
    }
    int minFlips(vector<vector<int>>& grid) {


           return total(grid);


    }
};
