class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        if len(grid) == 1:
            if len(grid[0]) < 4:
                return sum(sum(row) for row in grid)
        if len(grid[0]) == 1:
            if len(grid) < 4:
                return sum(sum(row) for row in grid)
        taken_care = False
        res = 0
        up, down = 0, len(grid) - 1
        while up < down:
            l, r = 0, len(grid[0]) - 1
            while l < r:
                s = grid[up][l] + grid[up][r] + grid[down][l] + grid[down][r]
                res += min(s, 4 - s)
                l += 1
                r -= 1
            if l == r:
                s = grid[up][l] + grid[down][l]
                if s == 1:
                    taken_care = True
                res += min(s, 2 - s)
            up += 1
            down -= 1
        if up == down:
            l, r = 0, len(grid[0]) - 1
            while l < r:
                s = grid[up][l] + grid[up][r]
                if s == 1:
                    taken_care = True
                res += min(s, 2 - s)
                l += 1
                r -= 1
        if len(grid) % 2 == 1 and len(grid[0]) % 2 == 1:
            res += grid[len(grid)//2][len(grid[0])//2]

        #print(taken_care)
        if not taken_care and (len(grid) % 2 == 1 or len(grid[0]) % 2 == 1):
            tmp = 0
            if len(grid) % 2 == 1:
                l, r = 0, len(grid[0]) - 1
                while l < r:
                    tmp += grid[len(grid)//2][l] + grid[len(grid)//2][r]
                    l += 1
                    r -= 1
            if len(grid[0]) % 2 == 1:
                #print('hai')
                up, down = 0, len(grid) - 1
                while up < down:
                    tmp += grid[up][len(grid[0])//2] + grid[down][len(grid[0])//2]
                    up += 1
                    down -= 1
            res += min(tmp % 4, 4 - tmp % 4)
            #print(tmp)
        return res
