class Solution {
public:

    int solver1(vector<vector<int>>&grid)
    {

        int n=grid.size();
        int m=grid[0].size();
        int ans=0;

        for(int i=0;i<n/2;i++){

            for(int j=0;j<m/2;j++)
            {

                int ct1=0;
                int ct0=0;

                if(grid[i][j]==0)ct0++;
                else ct1++;
                  if(grid[n-i-1][j]==0)ct0++;
                else ct1++;
                  if(grid[i][m-j-1]==0)ct0++;
                else ct1++;
                  if(grid[n-i-1][m-j-1]==0)ct0++;
                else ct1++;


                ans+=min(ct1,ct0);




            }
        }

        return ans;

    }

    int solver2(vector<int>&vec)

    {

        int shi1=0,shi0=0;
        int nhishi=0;
        int n=vec.size();

        for(int i=0;i<n/2;i++)
        {

            if(vec[i]==vec[n-i-1]){
                if(vec[i]==1)shi1++;
                else shi0++;
            }
            else nhishi++;

        }

        int ans=0;

        if(shi1%2!=0){
            if(nhishi==0)ans+=2;
            else ans+=nhishi;
        }
        else ans+=nhishi;
        return ans;


    }

    pair<int,int> solver3(vector<int>&vec)
    {
        int shi1=0,shi0=0;
        int nhishi=0;
        int n=vec.size();

        for(int i=0;i<n/2;i++)
        {

            if(vec[i]==vec[n-i-1]){
                if(vec[i]==1)shi1++;
                else shi0++;
            }
            else nhishi++;

        }

        return {shi1,nhishi};

    }
    int minFlips(vector<vector<int>>& grid)
    {



        int n=grid.size();

        int m=grid[0].size();

        int ans=solver1(grid);

        if(n%2!=0 and m%2==0)
        {

            vector<int>temp=grid[n/2];
            ans+=solver2(temp);

        }
        else if(n%2==0 and m%2!=0)
        {

            vector<int>temp;  

            for(int i=0;i<n;i++)temp.push_back(grid[i][m/2]);
            ans+=solver2(temp);

        }
        else if(n%2!=0 and m%2!=0)
        {

            if(grid[n/2][m/2]==1)ans++;
            auto it1=solver3(grid[n/2]);
            vector<int>temp;  

            for(int i=0;i<n;i++)temp.push_back(grid[i][m/2]);
            auto it2=solver3(temp);

            ans+=it1.second+it2.second;

            if((it1.first+it2.first)%2!=0){
                if(it1.second+it2.second==0)ans+=2;
            }

        }



        return ans;



    }
};
