class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int ans=0;
        int n=grid.size();
        int m=grid[0].size();

        if(n%2==0 && m%2==0)
        {
            for(int i=0; i<n/2; i++)
            {
                for(int j=0; j<m/2; j++)
                {
                    int cnt=0;
                    if(grid[i][j]==0) cnt++;
                    if(grid[i][m-j-1]==0) cnt++;
                    if(grid[n-i-1][j]==0) cnt++;
                    if(grid[n-i-1][m-j-1]==0) cnt++;

                    ans+=min(cnt,4-cnt);
                }
            }
        }

        else if(n%2==1 && m%2==1)
        {
            for(int i=0; i<n/2; i++)
            {
                for(int j=0; j<m/2; j++)
                {
                    int cnt=0;
                    if(grid[i][j]==0) cnt++;
                    if(grid[i][m-j-1]==0) cnt++;
                    if(grid[n-i-1][j]==0) cnt++;
                    if(grid[n-i-1][m-j-1]==0) cnt++;

                    ans+=min(cnt,4-cnt);
                }
            }

            int tmp=0;
            int cnt=0;
            for(int i=0; i<=m/2; i++)
            {
                if(grid[n/2][i]==1 && grid[n/2][m-i-1]==1) tmp++;
                if(grid[n/2][i]!=grid[n/2][m-i-1]) cnt++;
            }
            for(int i=0; i<=n/2; i++)
            {
                if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1) tmp++;
                if(grid[i][m/2]!=grid[n-i-1][m/2]) cnt++;
            }

            if(tmp%2)
            {
                if(cnt==0) cnt+=2;
            }

            ans+=cnt;
            if(grid[n/2][m/2]==1) ans++;

        }

        else if(n%2==0 && m%2==1)
        {

            for(int i=0; i<n/2; i++)
            {
                for(int j=0; j<m/2; j++)
                {
                    int cnt=0;
                    if(grid[i][j]==0) cnt++;
                    if(grid[i][m-j-1]==0) cnt++;
                    if(grid[n-i-1][j]==0) cnt++;
                    if(grid[n-i-1][m-j-1]==0) cnt++;

                    ans+=min(cnt,4-cnt);
                }
            }

            int cnt=0;
            int tmp=0;
            for(int i=0; i<n/2; i++)
            {
                if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1) tmp++;
                if(grid[i][m/2]!=grid[n-i-1][m/2]) cnt++;
            }

            if(tmp%2)
            {
                if(cnt==0) cnt+=2;
            }
            ans+=cnt;

            // cout<<cnt<<endl;
        }

        else
        {
            for(int i=0; i<n/2; i++)
            {
                for(int j=0; j<m/2; j++)
                {
                    int cnt=0;
                    if(grid[i][j]==0) cnt++;
                    if(grid[i][m-j-1]==0) cnt++;
                    if(grid[n-i-1][j]==0) cnt++;
                    if(grid[n-i-1][m-j-1]==0) cnt++;

                    ans+=min(cnt,4-cnt);
                }
            }

            int cnt=0;
            int tmp=0;
            for(int i=0; i<m/2; i++)
            {
                if(grid[n/2][i]==1 && grid[n/2][m-i-1]==1) tmp++;
                if(grid[n/2][i]!=grid[n/2][m-i-1]) cnt++;
            }

            if(tmp%2)
            {
                if(cnt==0) cnt+=2;
            }
            ans+=cnt;
        }
        return ans;
    }
};
